mapper.update:
UPDATE competition
competitionname = #{competitionname}, competitiondate = STR_TO_DATE(#{competitiondate}, '%Y-%m-%d'), competitionlevel = #{competitionlevel}, competitiontype = #{competitiontype}, competitiondescribe = #{competitiondescribe},
WHERE competitionid = #{competitionid}

mapper.select:
SELECT competitionid, competitionname, DATE_FORMAT(competitiondate, '%Y-%m') AS competitiondate, competitionlevel, competitiontype, competitiondescribe FROM competition 

    AND competitionid != '0'
    AND competitionid = #{competitionid}
    AND competitionname LIKE CONCAT(CONCAT('%',#{competitionname}),'%')
    AND competitiondate >= STR_TO_DATE(#{startdate},'%Y-%m')
    AND competitiondate <= STR_TO_DATE(#{enddate},'%Y-%m')
    AND competitionlevel = #{competitionlevel}
    AND competitiontype = #{competitiontype}

ORDER BY competitiondate DESC

SELECT competitionid, competitionname, DATE_FORMAT(competitiondate, '%Y-%m') AS competitiondate, competitionlevel, competitiontype, competitiondescribe FROM competition 

    AND competitionid != '0'
    AND competitionid = #{competitionid}
    AND competitionname LIKE CONCAT(CONCAT('%',#{competitionname}),'%')
    AND competitiondate >= STR_TO_DATE(#{startdate},'%Y-%m')
    AND competitiondate <= STR_TO_DATE(#{enddate},'%Y-%m')
    AND competitionlevel = #{competitionlevel}
    AND competitiontype = #{competitiontype}

ORDER BY competitiondate DESC

SELECT COUNT(*) AS recordnumber FROM competition 

    AND competitionid != '0'
    AND competitionid = #{competitionid}
    AND competitionname LIKE CONCAT(CONCAT('%',#{competitionname}),'%')
    AND competitiondate >= STR_TO_DATE(#{startdate},'%Y-%m')
    AND competitiondate <= STR_TO_DATE(#{enddate},'%Y-%m')
    AND competitionlevel = #{competitionlevel}
    AND competitiontype = #{competitiontype}
